import React from 'react';
import {Card, CardBody} from 'reactstrap';

import './TourCard.css'
import { Link } from 'react-router-dom';

 



const TourCard = ({tour}) => {
    const {id,title,city,photo,price,featured,reviews}=tour
    const totalRating=reviews?.reduce((acc,item)=> acc+item.rating,0);
    const avgRating=totalRating===0?"":totalRating===1?totalRating:totalRating/reviews?.length;
    

    return (
        <>
         <div className="tour__card">
            <Card>
                <div className="tour__img">
                    <img src={photo} alt="tour image" />
                    { featured && <span>Featured</span>}
                </div>
            </Card>
            
            <CardBody>
<div className="card__top d-flex align-item-center justify-content-center">
    <span className="tour__location d-flex align-items-center gap-1">
        <i className='ri-map-pin-line'></i>{city}
    </span>
    <span className="tour__rating d-flex align-items-center gap-1">
        <i className='ri-star-fill'></i>{avgRating===0?null:avgRating} 
        {totalRating===0?(
            'Not rated'
            ):(
            <span>({reviews.length})</span>
        )}
   <span>({reviews.length})</span>
    </span>

</div>

<h5 className="tour__title"><Link to={`/tours/${id}`}>{title}</Link></h5>
<div className="card__bottom d-flex align-items-center justify-content-between mt-3">
<h5>${price}<span>/per person</span></h5>
<button className='btn booking__btn'><Link to={`/tours/${id}`}>Book Now</Link></button>

</div>
            </CardBody>
            </div>   
        </>
    );
};

export default TourCard;
