import React from 'react';
// import tourData from '../../assets/data/tours';
import {Col} from 'reactstrap'
import TourCard from '../../shared/TourCard';

import tourImg01 from "../../assets/images/tour-img01.jpg";
import tourImg02 from "../../assets/images/tour-img02.jpg";
import tourImg03 from "../../assets/images/tour-img03.jpg";
import tourImg04 from "../../assets/images/tour-img04.jpg";
import tourImg05 from "../../assets/images/tour-img05.jpg";
import tourImg06 from "../../assets/images/tour-img06.jpg";
import tourImg07 from "../../assets/images/tour-img07.jpg";
const FeaturedList = () => {
     
    
    const tourData = [
      {
        id: "01",
        title: "Westminister Bridge",
        city: "London",
        distance: 300,
        price: 99,
        maxGroupSize: 10,
        desc: "this is the description",
        reviews: [
          {
            name: "jhon doe",
            rating: 4.6,
          },
        ],
        avgRating: 4.5,
        photo: tourImg01,
        featured: true,
      },
      {
        id: "02",
        title: "Bali, Indonesia",
        city: "Indonesia",
        distance: 400,
        price: 99,
        maxGroupSize: 8,
        desc: "this is the description",
        reviews: [
          {
            name: "jhon doe",
            rating: 4.6,
          },
        ],
        avgRating: 4.5,
        photo: tourImg02,
        featured: true,
      },
      {
        id: "03",
        title: "Snowy Mountains, Thailand",
        city: "Thailand",
        distance: 500,
        price: 99,
        maxGroupSize: 8,
        desc: "this is the description",
        reviews: [
          {
            name: "jhon doe",
            rating: 4.6,
          },
        ],
        avgRating: 4.5,
        photo: tourImg03,
        featured: true,
      },
      {
        id: "04",
        title: "Beautiful Sunrise, Thailand",
        city: "Thailand",
        distance: 500,
        price: 99,
        maxGroupSize: 8,
        desc: "this is the description",
        reviews: [
          {
            name: "jhon doe",
            rating: 4.6,
          },
        ],
        avgRating: 4.5,
        photo: tourImg04,
        featured: true,
      },
      {
        id: "05",
        title: "Nusa Pendia Bali, Indonesia",
        city: "Indonesia",
        distance: 500,
        price: 99,
        maxGroupSize: 8,
        desc: "this is the description",
        reviews: [
          {
            name: "jhon doe",
            rating: 4.6,
          },
        ],
        avgRating: 4.5,
        photo: tourImg05,
        featured: false,
      },
      {
        id: "06",
        title: "Cherry Blossoms Spring",
        city: "Japan",
        distance: 500,
        price: 99,
        maxGroupSize: 8,
        desc: "this is the description",
        reviews: [
          {
            name: "jhon doe",
            rating: 4.6,
          },
        ],
        avgRating: 4.5,
        photo: tourImg06,
        featured: false,
      },
      {
        id: "07",
        title: "Holmen Lofoten",
        city: "France",
        distance: 500,
        price: 99,
        maxGroupSize: 8,
        desc: "this is the description",
        reviews: [
          {
            name: "jhon doe",
            rating: 4.6,
          },
        ],
        avgRating: 4.5,
        photo: tourImg07,
        featured: false,
      },
      {
        id: "08",
        title: "Snowy Mountains, Thailand",
        city: "Thailand",
        distance: 500,
        price: 99,
        maxGroupSize: 8,
        desc: "this is the description",
        reviews: [
          {
            name: "jhon doe",
            rating: 4.6,
          },
        ],
        avgRating: 4.5,
        photo: tourImg03,
        featured: false,
      },
    ];
 
    
    return (
        <>
       {   
        tourData?.map(tour=>()=>{
            <Col lg='3' className='mb-4' key={tour.id}>
        <TourCard tour={tour}></TourCard>

            </Col>
        })
        }
        </>
    );
};

export default FeaturedList;
